<?php

namespace BasicDashboard\Web\Products\Resources;

use Illuminate\Http\Resources\Json\JsonResource;

/**
 *
 * A ProductResource is implement for sending data with requirements of desire template.
 * Generated By Custom Artisan Cmd
 * @author Nay Ba la
 * https://github.com/naybala
 * https://naybala.netlify.app/
 *
 */

class ProductResource extends JsonResource
{
    public function toArray($request): array
    {
        $photos = $this->photos ?? [];
        $photoUrls = array_map(fn($photo) => asset('storage/' . $photo), $photos);

        return [
            "id" => customEncoder($this->id),
            "name" => $this->name,
            "name_other" => $this->name_other,
            "price" => $this->price,
            "description" => $this->description,
            "description_other" => $this->description_other,
            "photos" => $photoUrls,
            "photo_paths" => $photos,
            "category_ids" => $this->categories->pluck('id')->toArray(),
            "category_names" => $this->categories->pluck('name')->toArray(),
            "primary_photo" => !empty($photoUrls) ? $photoUrls[0] : null,
        ];
    }
}
